mapper.select:
		SELECT 
    Name,
    (SELECT 
            COUNT(*)
        FROM
            fir_device
        WHERE
            DeviceTypeId= a.Id
            
            	and DeviceTypeId=#{deviceTypeId}
            
              
            	and CompanyId=#{companyId}
            
            
            	and AddTime<#{endTime}
            
            ) as DeviceNum,
              (SELECT 
            COUNT(*)
        FROM
            fir_checkdevice
        WHERE
            deviceTypeId = a.Id AND Status > 0
              
            	and DeviceTypeId=#{deviceTypeId}
            
              
            	and CompanyId=#{companyId}
            
             
            	and AddTime>#{startTime}
             
             
            	and AddTime<#{endTime}
            
            ) AS FailureNum,
    (SELECT 
            COUNT(*)
        FROM
            fir_checkdevice
        WHERE
            deviceTypeId = a.Id AND Status = 2
              
            	and DeviceTypeId=#{deviceTypeId}
            
              
            	and CompanyId=#{companyId}
            
               
            	and AddTime>#{startTime}
             
             
            	and AddTime<#{endTime}
            
            ) AS RectifiedNum
FROM
    fir_deviceType AS a where 1=1 
     
            	and a.Id=#{deviceTypeId}

SELECT DATE_FORMAT(PredictTime, '%m') Month, COUNT(*) as AssignmentCount, (SELECT COUNT(*) FROM fir_RepairRecord WHERE assignmentid = a.Id AND Addtime > a.PredictTime GROUP BY DATE_FORMAT(Addtime, '%m')) as CompleteCount FROM fir_assignment AS a where 1=1
and DATE_FORMAT(PredictTime, '%Y')=#{startTime}
and a.CompanyId=#{companyId}
GROUP BY Month;
