mapper.select:
		SELECT 
    Name,
    (SELECT 
            COUNT(*)
        FROM
            fir_device
        WHERE
            DeviceTypeId= a.Id
            
            	and DeviceTypeId=#{deviceTypeId}
            
              
            	and CompanyId=#{companyId}
            
            
            	and AddTime<#{endTime}
            
            ) as DeviceNum,
              (SELECT 
            COUNT(*)
        FROM
            fir_checkdevice
        WHERE
            deviceTypeId = a.Id AND Status > 0
              
            	and DeviceTypeId=#{deviceTypeId}
            
              
            	and CompanyId=#{companyId}
            
             
            	and AddTime>#{startTime}
             
             
            	and AddTime<#{endTime}
            
            ) AS FailureNum,
    (SELECT 
            COUNT(*)
        FROM
            fir_checkdevice
        WHERE
            deviceTypeId = a.Id AND Status = 2
              
            	and DeviceTypeId=#{deviceTypeId}
            
              
            	and CompanyId=#{companyId}
            
               
            	and AddTime>#{startTime}
             
             
            	and AddTime<#{endTime}
            
            ) AS RectifiedNum
FROM
    fir_deviceType AS a where 1=1 
     
            	and a.Id=#{deviceTypeId}
